function solicita_numar(tip_numar) {
    let numar = 0;
	let este_valid = false;

	while (!este_valid) {		
		numar = prompt(tip_numar);

		if (tip_numar == 'negativ') {
			este_valid = (numar <= 0);
		} else {
			este_valid = (numar >= 0);
		}		
	}

    return numar;
}

